Array.prototype.sum=function(){var t=0;for(var i=0;i<this.length;i++){t+=this[i]}return t};
Array.prototype.average=function(){return this.sum()/this.length};
Array.prototype.range=function(){var self=this.sort();return{min:self[0],max:self[this.length-1]}};
Array.prototype.first=function(){return this[0]};
Array.prototype.last=function(){return this[this.length-1]};
Array.prototype.shuffle=function(){let s=this;for(let i=s.length-1;i>0;i--){const j=Math.floor(Math.random()*(i+1));[s[i],s[j]]=[s[j],s[i]]}return s}
